                    l, r = queries[i]
                    for j in range(l, r + 1):
                        dec = min(temp_nums[j],1)
                        temp_nums[j] -= dec
                        total_sum -= dec

            return all(x == 0 for x in temp_nums)

        left, right = 0, len(queries)
