        n = len(fruits)
        ans = 0

        def calculate_fruits(path1, path2, path3):
            visited = set()
            total_fruits = 0
            for r, c in path1:
                if (r,c) not in visited:
                    total_fruits += fruits[r][c]
                    visited.add((r,c))
            for r, c in path2:
                if (r,c) not in visited:
                    total_fruits += fruits[r][c]
                    visited.add((r,c))
            for r, c in path3:
