        queue = collections.deque([start])
        visited = {start}
        distances = {start: 0}
        while queue:
            curr = queue.popleft()
            for neighbor in graph[curr]:
                if neighbor not in visited:
                    visited.add(neighbor)
                    distances[neighbor] = distances[curr] + 1
                    queue.append(neighbor)
        return distances

    max_dist = 0
    for u in range(1, n1 + 1):
