        for i in range(1, n + 1):
            count *= math.comb(nk - sum(arr[:i]), k)
        return count

    nk = n * k

    total_sequences = math.factorial(nk)
    for i in range(1, n + 1):
        total_sequences //= math.factorial(k)

    target_index = (total_sequences + 1) // 2 -1

