                u = leaves[i]
                v = leaves[j]
                if u in removed or v in removed:
                    continue

                q = collections.deque([(u, 0)])
                visited = {u}
                while q:
                    curr, dist = q.popleft()
                    if curr == v:
                        if dist > best_dist:
