			if a > 0 and last != 'A':
				res = max(res, 2 + dp(a-1, b, c, 'A'))
			if b > 0 and last != 'B':
				res = max(res, 2 + dp(a, b-1, c, 'B'))
			if c > 0:
				res = max(res, 2 + dp(a, b, c-1, 'B'))
			return res

		return dp(x, y, z, '')

