class Solution:
	def maximumJumps(self, nums: List[int], target: int) -> int:
		n = len(nums)
		dp = [-1] * n
		dp[0] = 0
		for i in range(n):
			if dp[i] == -1:
				continue
			for j in range(i + 1, n):
				if abs(nums[j] - nums[i]) <= target:
					if dp[j] < dp[i] + 1:
						dp[j] = dp[i] + 1
		return dp[n - 1]

