		ans = 0
		for x in range(n):
			cur = x
			total = x
			for j in range(MAX_LOG):
				if (k >> j) & 1:
					total += sum_dp[j][cur]
					cur = dp[j][cur]
			if total > ans:
				ans = total
		return ans

