		n = len(s)
		total_freq = [0] * 26
		for char in s:
			idx = ord(char) - ord('a')
			total_freq[idx] += 1
		for count in total_freq:
			if count % 2 != 0:
				return [False] * len(queries)

		prefix = [[0] * 26 for _ in range(n+1)]
		for i in range(1, n+1):
			prefix[i] = prefix[i-1][:]
			char_idx = ord(s[i-1]) - ord('a')
			prefix[i][char_idx] += 1

		def get_freq(l, r):
			if l > r:
