			res = [0] * 26
			for idx in range(26):
				res[idx] = prefix[r+1][idx] - prefix[l][idx]
			return res

		half = n // 2
		mismatches = []
		for i in range(half):
			if s[i] != s[n-1-i]:
				mismatches.append(i)
		mismatches.sort()

		def has_mismatch_in_interval(l, r):
			if l > r:
				return False
			pos = bisect.bisect_left(mismatches, l)
			return pos < len(mismatches) and mismatches[pos] <= r

		ans = []
		for q in queries:
