				continue

			l3 = max(b, n - 1 - c) + 1
			r3 = half - 1
			if l3 <= r3 and has_mismatch_in_interval(l3, r3):
				ans.append(False)
				continue

			l4 = b + 1
			r4 = n - 1 - d - 1
			if l4 <= r4 and has_mismatch_in_interval(l4, r4):
				ans.append(False)
				continue

			freqF = get_freq(a, b)
			freqG = get_freq(c, d)

			freqA = [0] * 26
			lA1 = n - 1 - b
			rA1 = min(n - 1 - a, c - 1)
			if lA1 <= rA1:
				fA1 = get_freq(lA1, rA1)
				for idx in range(26):
					freqA[idx] += fA1[idx]

			lA2 = max(n - 1 - b, d + 1)
			rA2 = n - 1 - a
			if lA2 <= rA2:
				fA2 = get_freq(lA2, rA2)
				for idx in range(26):
					freqA[idx] += fA2[idx]

			freqB = [0] * 26
			lB1 = n - 1 - d
			rB1 = min(n - 1 - c, a - 1)
			if lB1 <= rB1:
				fB1 = get_freq(lB1, rB1)
				for idx in range(26):
					freqB[idx] += fB1[idx]

			lB2 = max(n - 1 - d, b + 1)
			rB2 = n - 1 - c
			if lB2 <= rB2:
				fB2 = get_freq(lB2, rB2)
				for idx in range(26):
					freqB[idx] += fB2[idx]

			valid = True
			for idx in range(26):
				if freqF[idx] < freqA[idx] or freqG[idx] < freqB[idx]:
					valid = False
					break
				if (freqF[idx] - freqA[idx]) != (freqG[idx] - freqB[idx]):
					valid = False
					break
			ans.append(valid)

		return ans

