	def minimumCoins(self, prices: List[int]) -> int:
		n = len(prices)
		INF = 10**15
		dp = [INF] * (n + 1)
		g = [-1] * (n + 1)

		dp[0] = 0
		g[0] = -1

		for i in range(n):
			if dp[i] == INF:
				continue

			for j in range(0, i):
				new_cov = min(2 * j + 1, n - 1)
				total_cov = max(g[i], new_cov)
				new_state = total_cov + 1
				if new_state > n:
