	hasM = any(m in s for s in sets)
	if not has1 or not hasM:
		print(-1)
		return

	sets_per_element = [[] for _ in range(m+1)]
	for i, s in enumerate(sets):
		for e in s:
			if 1 <= e <= m:
				sets_per_element[e].append(i)

	dist = [-1] * n
	visited_element = [False] * (m+1)
	q = deque()

	for i in range(n):
		if 1 in sets[i]:
			dist[i] = 0
			q.append(i)

	while q:
		i = q.popleft()
		for e in sets[i]:
