		rq = find(q_val)
		if rp == rq:
			output_lines.append("Yes")
		else:
			if rp > rq:
				rp, rq = rq, rp
			if (rp, rq) in forbidden_set:
				output_lines.append("No")
			else:
				output_lines.append("Yes")

	print("\n".join(output_lines))

