	dp = [[INF] * (MAX_SKIP + 1) for _ in range(N)]
	dp[0][0] = 0.0

	for j in range(1, N):
		for t in range(0, MAX_SKIP + 1):
			low_bound = max(0, j - t - 1)
			for i in range(low_bound, j):
				k0 = t - (j - i - 1)
				if k0 < 0 or k0 > MAX_SKIP:
					continue
				x1, y1 = points[i]
				x2, y2 = points[j]
