		num_b = flat[b]
		num_c = flat[c]
		if num_a == num_b == num_c:
			continue
		if len({num_a, num_b, num_c}) == 2:
			if num_a == num_b:
				diff_index = c
			elif num_a == num_c:
				diff_index = b
			else:
				diff_index = a
			dangerous_lines.append((line, diff_index))

