def count_in_subtree(root, d, N):
	if d < 0:
		return 0
	if root > N:
		return 0
	max_d = N.bit_length() - root.bit_length()
	if d > max_d:
		return 0
	low = root << d
	if low > N:
		return 0
	num_nodes = 1 << d
	high = low + num_nodes - 1
	if high > N:
		return N - low + 1
	else:
		return num_nodes

