	for _ in range(t):
		N = int(data[index])
		X = int(data[index+1])
		K = int(data[index+2])
		index += 3
		count1 = count_in_subtree(X, K, N)
		count2 = 0
		a = 1
		current = X
		while current != 1 and a <= K:
			parent = current // 2
			if a == K:
				count2 += 1
			if current == 2 * parent:
				sibling = 2 * parent + 1
			else:
				sibling = 2 * parent
			if sibling <= N:
