		a1 = max(0, 9 - x)
		b1 = min(24, 17 - x)
		if a1 < b1:
			critical_points.add(a1)
			critical_points.add(b1)

		a2 = max(0, 33 - x)
		b2 = min(24, 41 - x)
		if a2 < b2:
			critical_points.add(a2)
			critical_points.add(b2)

