import sys

def main():
	data = sys.stdin.read().split()
	it = iter(data)
	n = int(next(it))
	A = int(next(it))
	B = int(next(it))
	C = int(next(it))
	D = []
	for i in range(n):
		row = []
		for j in range(n):
			row.append(int(next(it)))
		D.append(row)

	INF = 10**18
	dist0 = [INF] * n
	dist1 = [INF] * n
	dist0[0] = 0
	dist1[0] = 0

	heap = []
	heapq.heappush(heap, (0, 0, 0))
	heapq.heappush(heap, (0, 0, 1))

	while heap:
