		v = int(data[idx+1])
		w = int(data[idx+2])
		idx += 3
		edges.append((u-1, v-1, w))

	min_cost = K_val
	if n == 1:
		print(0)
		return

	from itertools import combinations
	for subset in combinations(edges, n-1):
		total = 0
		for edge in subset:
			total += edge[2]

		parent = list(range(n))
		rank = [0] * n
		valid = True
		for (u, v, w) in subset:
			ru = find(u, parent)
			rv = find(v, parent)
			if ru == rv:
				valid = False
				break
			union(ru, rv, parent, rank)

		if not valid:
			continue

		candidate = total % K_val
		if candidate < min_cost:
			min_cost = candidate
			if min_cost == 0:
