import sys

def feasible(S, Q, A, B):
	n = len(Q)
	x_lower = 0
	x_upper = S
	for i in range(n):
		d = A[i] - B[i]
		num = Q[i] - S * B[i]
		if d == 0:
			if num < 0:
				return False
		elif d > 0:
			if num < 0:
				return False
			x_upper = min(x_upper, num // d)
		else:
			if num < 0:
				num_abs = -num
				d_abs = -d
				bound = (num_abs + d_abs - 1) // d_abs
				x_lower = max(x_lower, bound)
		if x_lower > x_upper:
			return False
	return x_lower <= x_upper

