import sys

class FenwTree:
	def __init__(self, size):
		self.n = size
		self.tree = [0] * (self.n + 1)

	def update(self, index, val):
		i = index + 1
		while i <= self.n:
			self.tree[i] ^= val
			i += i & -i

	def query(self, index):
		i = index + 1
		res = 0
		while i:
			res ^= self.tree[i]
			i -= i & -i
		return res

class SegmentTree:
	def __init__(self, data):
		self.n = len(data)
		self.size = 1
		while self.size < self.n:
			self.size *= 2
		self.tree = [0] * (2 * self.size)
		for i in range(self.n):
			self.tree[self.size + i] = data[i]
		for i in range(self.size - 1, 0, -1):
			self.tree[i] = self.tree[2 * i] | self.tree[2 * i + 1]

	def update(self, index, value):
		i = self.size + index
		self.tree[i] = value
		i //= 2
		while i:
			self.tree[i] = self.tree[2 * i] | self.tree[2 * i + 1]
			i //= 2

	def query(self, l, r):
		l += self.size
		r += self.size
		res = 0
		while l <= r:
			if l % 2 == 1:
				res |= self.tree[l]
				l += 1
			if r % 2 == 0:
				res |= self.tree[r]
				r -= 1
			l //= 2
			r //= 2
		return res

