	ans = [0] * (N+1)

	for j in range(1, N+1):
		lst = events[j]
		k = len(lst)
		for p in range(0, k, 2):
			start = lst[p]
			if p+1 < k:
				end = lst[p+1] - 1
			else:
				end = Q - 1
			if start <= end:
				if start == 0:
					total_val = prefix[end]
				else:
					total_val = prefix[end] - prefix[start-1]
				ans[j] += total_val

	res = []
	for i in range(1, N+1):
		res.append(str(ans[i]))
	print(" ".join(res))

