			heapq.heappush(heap, (cnt + 1, idx))

		target_freq = [0] * 26
		while heap:
			cnt, idx = heapq.heappop(heap)
			target_freq[idx] = cnt

		current_freq = [0] * 26
		remaining_fixed = total_fixed_freq[:]
		res = []

