import math

def integer_kth_root(N, k):
	if k == 1:
		return N
	if k == 2:
		return math.isqrt(N)
	low, high = 1, 1
	while True:
		if high > 10**7:
			break
		try:
			power = pow(high, k)
		except OverflowError:
			break
		if power < N:
			low = high
			high *= 2
		elif power == N:
			return high
		else:
			break
	if high > 10**7:
		high = 10**7
	while low <= high:
		mid = (low + high) // 2
		try:
			mid_power = pow(mid, k)
		except OverflowError:
			high = mid - 1
			continue
		if mid_power == N:
			return mid
		elif mid_power < N:
			low = mid + 1
		else:
			high = mid - 1
	return high

