	H = int(data[0])
	W = int(data[1])
	Y = int(data[2])
	grid = []
	index = 3
	for i in range(H):
		row = list(map(int, data[index:index+W]))
		grid.append(row)
		index += W

	if H == 0 or W == 0:
		for _ in range(Y):
			print(0)
		return

	INF = 10**9
	d = [[INF] * W for _ in range(H)]
	heap = []
	dirs = [(0,1), (0,-1), (1,0), (-1,0)]

	for i in range(H):
		for j in range(W):
			if i == 0 or i == H-1 or j == 0 or j == W-1:
				d[i][j] = grid[i][j]
				heapq.heappush(heap, (d[i][j], i, j))

	while heap:
		val, i, j = heapq.heappop(heap)
		if val != d[i][j]:
			continue
		for dx, dy in dirs:
			ni, nj = i + dx, j + dy
			if 0 <= ni < H and 0 <= nj < W:
				new_val = max(val, grid[ni][nj])
				if new_val < d[ni][nj]:
					d[ni][nj] = new_val
					heapq.heappush(heap, (new_val, ni, nj))

