class Fenw:
	def __init__(self, size):
		self.n = size
		self.tree = [0] * (self.n + 1)

	def update(self, idx, delta):
		i = idx + 1
		while i <= self.n:
			self.tree[i] += delta
			i += i & -i

	def query(self, idx):
		if idx < 0:
			return 0
		i = idx + 1
		s = 0
		while i:
			s += self.tree[i]
			i -= i & -i
		return s

