					diff[idx] = new_val
					if new_val == 0:
						k_curr = len(zeros)
						if k_curr == 0:
							fenw_count.update(n, -1)
							fenw_sum.update(n, -(n + 1))
							fenw_count.update(n - 1, 1)
							fenw_sum.update(n - 1, n)
							bisect.insort(zeros, idx)
						else:
							pos_idx = bisect.bisect_left(zeros, idx)
							if pos_idx == 0:
								left_zero = zeros[-1]
							else:
								left_zero = zeros[pos_idx - 1]
							if pos_idx == k_curr:
								right_zero = zeros[0]
