							new_gap = gap_len(left_zero, right_zero, n)
							fenw_count.update(g1, -1)
							fenw_sum.update(g1, -(g1 + 1))
							fenw_count.update(g2, -1)
							fenw_sum.update(g2, -(g2 + 1))
							fenw_count.update(new_gap, 1)
							fenw_sum.update(new_gap, new_gap + 1)
							zeros.pop(pos_idx)
		return ans

