import math
from collections import deque
from fractions import Fraction

def floor_sum(n, m, a, b):
	ans = 0
	if a < 0:
		a2 = a % m
		if a2 < 0:
			a2 += m
		cnt = (a2 - a) // m
		ans -= n * (n - 1) // 2 * cnt
		a = a2
	if b < 0:
		b2 = b % m
		if b2 < 0:
			b2 += m
		cnt = (b2 - b) // m
		ans -= n * cnt
		b = b2
	while True:
		if a >= m:
			cnt = a // m
			ans += n * (n - 1) // 2 * cnt
			a %= m
		if b >= m:
			cnt = b // m
			ans += n * cnt
			b %= m
		y_max = a * n + b
		if y_max < m:
			break
		n = y_max // m
		b = y_max % m
		m, a = a, m
		ans += (n - 1) * n // 2 * (a // m)
		a %= m
	return ans

def line_intersection(line1, line2):
	a1, b1, c1 = line1
	a2, b2, c2 = line2
	denom = a1 * b2 - a2 * b1
	if denom == 0:
		return None
	x = Fraction(b1 * c2 - b2 * c1, denom)
	y = Fraction(a2 * c1 - a1 * c2, denom)
	return (x, y)

def is_point_in_half_plane(point, line):
	x, y = point
	a, b, c = line
	return a * x + b * y + c <= 0

def half_plane_intersection(lines):
	lines = [tuple(line) for line in lines]
	unique_lines = {}
	for line in lines:
		a, b, c = line
		if a == 0 and b == 0:
			if c < 0:
				return []
			else:
				continue
		g = math.gcd(math.gcd(a, b), c)
		a //= g
		b //= g
		c //= g
		if a < 0 or (a == 0 and b < 0):
			a, b, c = -a, -b, -c
		key = (a, b)
		if key not in unique_lines:
			unique_lines[key] = (a, b, c)
		else:
			a0, b0, c0 = unique_lines[key]
			if c < c0:
				unique_lines[key] = (a, b, c)
	lines = list(unique_lines.values())
	if not lines:
		return []
	lines.sort(key=lambda line: (math.atan2(line[1], line[0])))
	def cross_angle(line1, line2):
		a1, b1, c1 = line1
		a2, b2, c2 = line2
		return a1 * b2 - a2 * b1
	sorted_lines = []
	for i in range(len(lines)):
		if i > 0 and cross_angle(lines[i], lines[i-1]) == 0:
			continue
		sorted_lines.append(lines[i])
	lines = sorted_lines
	dq = deque()
	for i in range(len(lines)):
		while len(dq) >= 2:
			p = line_intersection(dq[-1], dq[-2])
			if p is None:
				dq.pop()
				continue
			if is_point_in_half_plane(p, lines[i]):
				break
			dq.pop()
		while len(dq) >= 2:
			p = line_intersection(dq[0], dq[1])
			if p is None:
				dq.popleft()
				continue
			if is_point_in_half_plane(p, lines[i]):
				break
			dq.popleft()
		if not dq:
			dq.append(lines[i])
		else:
			if line_intersection(dq[-1], lines[i]) is None:
