import math
from collections import deque
from fractions import Fraction

def floor_sum(n, m, a, b):
	ans = 0
	if a < 0:
		a2 = a % m
		if a2 < 0:
			a2 += m
		cnt = (a2 - a) // m
		ans -= n * (n - 1) // 2 * cnt
		a = a2
	if b < 0:
		b2 = b % m
		if b2 < 0:
			b2 += m
		cnt = (b2 - b) // m
		ans -= n * cnt
		b = b2
	while True:
		if a >= m:
			cnt = a // m
			ans += n * (n - 1) // 2 * cnt
			a %= m
		if b >= m:
			cnt = b // m
			ans += n * cnt
			b %= m
		y_max = a * n + b
		if y_max < m:
			break
		n = y_max // m
		b = y_max % m
		m, a = a, m
		ans += (n - 1) * n // 2 * (a // m)
		a %= m
	return ans

def line_intersection(line1, line2):
	a1, b1, c1 = line1
	a2, b2, c2 = line2
	denom = a1 * b2 - a2 * b1
	if denom == 0:
		return None
	x = Fraction(b1 * c2 - b2 * c1, denom)
	y = Fraction(a2 * c1 - a1 * c2, denom)
	return (x, y)

