	if rem < 0:
		print(" ".join(["-1"] * n))
		return

	b = sorted(A, reverse=True)
	d = [0] * (n+1)
	for i in range(1, n+1):
		d[i] = d[i-1] + b[i-1]

	ans = [-1] * n

	def check(i, X):
		T = A[i] + X
		lo, hi = 0, n
		while lo < hi:
			mid = (lo + hi) // 2
			if b[mid] > T:
				lo = mid + 1
			else:
				hi = mid
		count_above = lo

		if count_above >= M:
			return False

		k0 = M - count_above
		if k0 <= 0:
			return True

		total_in_set = n - count_above
		if k0 > total_in_set - 1:
			if n - 1 < M:
				return True
			else:
				return False

		start_index = count_above
		end_index = count_above + k0 - 1
		S_total = d[end_index+1] - d[start_index]

