import random
import sys

class Fenw:
	def __init__(self, size):
		self.n = size
		self.fw = [0] * (self.n + 1)

	def update(self, index, delta):
		i = index
		while i <= self.n:
			self.fw[i] += delta
			i += i & -i

	def query(self, index):
		s = 0
		i = index
		while i > 0:
			s += self.fw[i]
			i -= i & -i
		return s

class Node:
	__slots__ = ('value', 'priority', 'left', 'right', 'parent', 'size', 'max_val', 'max_node')
	def __init__(self, value, priority):
		self.value = value
		self.priority = priority
		self.left = None
		self.right = None
		self.parent = None
		self.size = 1
		self.max_val = value
		self.max_node = self

def update_node(node):
	if node is None:
		return
	node.size = 1
	node.max_val = node.value
	node.max_node = node
	if node.left is not None:
		node.size += node.left.size
		if node.left.max_val > node.max_val:
			node.max_val = node.left.max_val
			node.max_node = node.left.max_node
	if node.right is not None:
		node.size += node.right.size
		if node.right.max_val > node.max_val:
			node.max_val = node.right.max_val
			node.max_node = node.right.max_node

def treap_merge(left, right):
	if left is None:
		return right
	if right is None:
		return left
	if left.priority > right.priority:
		left.right = treap_merge(left.right, right)
		if left.right is not None:
			left.right.parent = left
		update_node(left)
		return left
	else:
		right.left = treap_merge(left, right.left)
		if right.left is not None:
			right.left.parent = right
		update_node(right)
		return right

def treap_split(node, key):
	if node is None:
		return (None, None)
	left_size = node.left.size if node.left is not None else 0
	if key <= left_size:
		l, r = treap_split(node.left, key)
		node.left = r
		if r is not None:
			r.parent = node
		update_node(node)
		if l is not None:
			l.parent = None
		return (l, node)
	else:
		l, r = treap_split(node.right, key - left_size - 1)
		node.right = l
		if l is not None:
			l.parent = node
		update_node(node)
		if r is not None:
			r.parent = None
		return (node, r)

