			dpA = new_dpA
			A_prefix[i] = dpA[k]

		dpB = [set() for _ in range(k+1)]
		dpB[0].add(0)
		B_suffix = [set() for _ in range(n)]

		for i in range(n-1, -1, -1):
			new_dpB = [set() for _ in range(k+1)]
			for b in range(k+1):
				new_dpB[b] = set(dpB[b])
