				if dp[a][b] != INF:
					dist[a][b] = dp[a][b]
					q.append((a, b))

		while q:
			a, b = q.popleft()
			current_cost = dist[a][b]
			moves = [
				((a + 1) % N, b),
				((a - 1 + N) % N, b),
				(a, (b + 1) % N),
				(a, (b - 1 + N) % N)
			]
			for na, nb in moves:
				if na == nb:
