import sys

class SegmentTree:
	def __init__(self, n, mode):
		self.n = n
		self.size = 1
		while self.size < n:
			self.size *= 2
		self.mode = mode
		if mode == 'max':
			self.default = -10**18
		else:
			self.default = 10**18
		self.data = [self.default] * (2 * self.size)

	def update(self, index, value):
		i = index + self.size
		if self.mode == 'max':
			if value > self.data[i]:
				self.data[i] = value
		else:
			if value < self.data[i]:
				self.data[i] = value
		i //= 2
		while i:
			if self.mode == 'max':
				self.data[i] = max(self.data[2*i], self.data[2*i+1])
			else:
				self.data[i] = min(self.data[2*i], self.data[2*i+1])
			i //= 2

	def query(self, l, r):
		l += self.size
		r += self.size
		if self.mode == 'max':
			res = self.default
			while l <= r:
				if l % 2 == 1:
					res = max(res, self.data[l])
					l += 1
				if r % 2 == 0:
					res = max(res, self.data[r])
					r -= 1
				l //= 2
				r //= 2
			return res
		else:
			res = self.default
			while l <= r:
				if l % 2 == 1:
					res = min(res, self.data[l])
					l += 1
				if r % 2 == 0:
					res = min(res, self.data[r])
					r -= 1
				l //= 2
				r //= 2
			return res

