		b0 = b - 1
		vars_set = {a0, b0, a0 + n}
		equations.append([set(vars_set), c])
		for v in vars_set:
			occ[v].add(i)

	sol = [None] * total_vars
	q = deque()

	for i in range(m):
		if len(equations[i][0]) == 1:
			q.append(i)

