	def decompose(l, r):
		res = []
		i = l
		while i <= r:
			cnt = 0
			j = i
			while j <= r:
				if s[j] == '(':
					cnt += 1
				else:
					cnt -= 1
				if cnt == 0:
					break
				j += 1
			if cnt != 0:
				break
			res.append((i, j))
			i = j + 1
		return res

