		n = len(skill)
		m = len(mana)
		if m == 0:
			return 0

		F_prev = [0] * n

		for j in range(m):
			constraints = [F_prev[0]]
			s = 0
			for i in range(1, n):
				s += skill[i-1]
				constraints.append(F_prev[i] - s * mana[j])
