class Solution:
    def countBeautifulPairs(self, nums: List[int]) -> int:
        first_digits = [int(str(num)[0]) for num in nums]
        last_digits = [num % 10 for num in nums]

        first_digits_count = Counter(first_digits)
        last_digits_count = Counter(last_digits)

        beautiful_pairs = 0

