            max_in_prefix[i] = max(max_in_prefix[i-1], nums[i])

        partitions = 1
        for i in range(n):
            if max_in_prefix[i] <= i and i == n-1 or max_in_prefix[i] <= i and i < n-1 and last_occurrence[nums[i]] == i:
                partitions = (partitions * 2) % mod
        return partitions

