    max_pieces = [0] * (N + 1)
    for v in range(1, N + 1):
        max_pieces[v] = pieces[v - 1]
        for u in edges[v]:
            if weights[u - 1] < weights[v - 1]:
                max_pieces[v] += max_pieces[u]

    # Use a priority queue to process vertices in decreasing order of weight
    pq = []
    for v in range(1, N + 1):
        heappush(pq, (-weights[v - 1], v))
    answer = 0

