        _, v = heappop(pq)
        if max_pieces[v] > 0:
            answer += 1
            max_pieces[v] -= 1
            for u in edges[v]:
                if weights[u - 1] < weights[v - 1]:
                    max_pieces[u] -= 1

    print(answer)

