MOD = 998244353

def modpow(x, n):
    if n == 0:
        return 1
    elif n % 2 == 1:
        return x * modpow(x, n - 1) % MOD
    else:
        y = modpow(x, n // 2)
        return y * y % MOD

def modinv(x):
    return modpow(x, MOD - 2)

def comb_modp(n, k):
    if k < 0 or k > n:
        return 0
    elif k == 0 or k == n:
        return 1
    else:
        numerator = 1
        for i in range(n, n - k, -1):
            numerator = numerator * i % MOD
        denominator = modpow(k, MOD - 2)
        return numerator * denominator % MOD

def main():
