            for j in range(C[i] + 1):
                dp[k][j + 1] += dp[k - 1][j] * comb_modp(k, j)
                dp[k][j + 1] %= MOD

    ans = 0
    for i in range(1, 27):
        ans += dp[K][i]
        ans %= MOD

    print(ans)

