from itertools import accumulate

def solve(A,B,C):
    g = list(accumulate([gcd(a,b) for a,b in zip(A,B)],gcd))
    def f(x,y):
        return sum((a*x+b*y<c) for a,b,c in zip(A,B,C))
    def ok(k):
        if k==0:
            return f(1,1)
        k = -k
        l = 1
        r = 10**18
        while l<r:
            m = (l+r)//2
            if f(k//g[k-1],m)>0:
                r = m
            else:
                l = m+1
        return f(k//g[k-1],l)
